var Code=require("code"),Lab=require("lab"),expect=Code.expect,lab=exports.lab=Lab.script(),phoneType=require("../lib/phonelibtype"),phonelib=require("../");lab.experiment("Check numbers",function(){lab.test("Check a valid phone number using phonelib",function(e){var n="+1 202-456-1414",o=!0;phonelib.isValid({phone:"2024561414",country:"US"},function(i,t){i&&e(i),expect(t.international).to.be.equal(n),expect(t.isValid).to.be.equal(o),e()})}),lab.test("Check an invalid phone number using phonelib",function(e){var n=!1;phonelib.isValid({phone:"88888888",country:"GT"},function(o,i){o&&e(o),expect(i.isValid).to.be.equal(n),e()})}),lab.test("Check number type",function(e){var n={numberType:phoneType.phoneNumberType.MOBILE};phonelib.isValid({phone:"40570609",country:"GT"},function(o,i){o&&e(o),expect(i.numberType).to.be.equal(n.numberType),e()})}),lab.test("Check if error is properly returned",function(e){phoneType.phoneNumberType.MOBILE;phonelib.isValid({phone:"1",country:"GT"},function(n,o){n?(expect(n.toString()).to.be.equal("Error: The string supplied did not seem to be a phone number"),e()):e("Should be an error.")})})});